
  CREATE OR REPLACE FORCE VIEW "UGH"."V_VEHTRIPS_MOTH_3M" ("TRIPCOUNTER", "VEHID", "TIMELOAD", "TIMEUNLOAD", "GMTTIMELOAD", "GMTTIMEUNLOAD", "FUELLOAD", "FUELUNLOAD", "WEIGHT", "LENGTH", "AVSPEED", "UNLOADLENGTH", "SHOVID", "UNLOADID", "LOADTYPE", "MOVETIME", "XLOAD", "YLOAD", "XUNLOAD", "YUNLOAD", "WORKTYPE", "AREA", "VEHCODE", "SHOV_ID", "UNLOAD_ID", "LOADTYPE_ID", "WORKTYPE_ID", "AREA_ID", "HYDROSYSTEMWEIGHT", "TIME_INSERTING", "UNLOADIDFORLOAD", "ZLOAD", "ZUNLOAD", "WRATE", "VRATE", "REDUCEDLENGTH", "ISAREAINTERSECT", "BUCKETCOUNT", "LOADHEIGHT", "UNLOADHEIGHT") AS 
