 from dispatcher.vehtrips v 
 where v.timeload  
 BETWEEN dispatcher.GetPredefinedTimeFrom ( /*:ParamDataFrom*/ 'за указанный интервал' , 1 , SYSDATE-90, /*:ParamBeginPeriod*/ to_date('01.'||to_char(SYSDATE-90,'mm')||'.'||to_char(SYSDATE-90,'yyyy')||' 08:00:00','dd.mm.yyyy hh24:mi:ss'))  
 AND dispatcher.GetPredefinedTimeTo ( /*:ParamDataFrom*/ 'за указанный интервал' , 1 , SYSDATE, /*:ParamEndPeriod*/ sysdate);
